 //
//	Converts a value to result as generated by Windows machines
//
//	@param  string -   The value to convert
//	@param  to -  The property to set
//

var string = attributes.get("string");

project.setProperty(
  attributes.get("to"), "file:/" + string.replace(/\\/g, '\/')
);